function init(){$("#my-navbar .col-xs-3").eq(0).addClass("active")}function bannerToggle(){$("#main-carousel").on("swipeleft",function(){$(".right.carousel-control").click()}).on("swiperight",function(){$(".left.carousel-control").click()})}function classifyTogggle(){$("#praise-classify .js-classify-toggle").on("swipe",function(){var i=$(this).index();$(this).removeClass("activity").siblings(".js-classify-toggle").addClass("activity"),$(this).siblings(".toggle-circle-list").children(".toggle-circle-item").eq(i).removeClass("activity").siblings(".toggle-circle-item").addClass("activity")})}function bottomNavbarClick(){$("#my-navbar li a").click(function(){$(document).scrollTop(0)})}function whichPage(){var i=$("#my-mainbody>.tab-pane.active").index();return i}$(document).ready(function(){init(),bannerToggle(),classifyTogggle(),bottomNavbarClick()});